    private void BtnCari1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnCari1ActionPerformed
        // Kembalian E-resep
        this.setCursor(Cursor.getPredefinedCursor(Cursor.WAIT_CURSOR));
        try{
            ps7=koneksi.prepareStatement("select no_resep from resep_obat where no_rawat = ? ORDER BY no_resep DESC LIMIT 1");
            try{
                ps7.setString(1,TNoRw.getText());
                rs2=ps7.executeQuery();
                
                if(rs2.next()){
                    //Ambil Resep Obat Integrasi
                    ps8=koneksi.prepareStatement("select databarang.nama_brng, resep_dokter.aturan_pakai from resep_dokter "+
                                                "inner join resep_obat inner join databarang on resep_dokter.no_resep = resep_obat.no_resep "+
                                                "and resep_dokter.kode_brng = databarang.kode_brng where resep_obat.no_resep = ?");
                    ps8.setString(1,rs2.getString(1));
                    rs3=ps8.executeQuery();
                    
                    ps12=koneksi.prepareStatement("select databarang.nama_brng, resep_dokter_racikan_detail.p1,resep_dokter_racikan_detail.p2,resep_dokter_racikan_detail.kandungan " +
                                                "from resep_dokter_racikan_detail " +
                                                "inner join databarang " +
                                                "on databarang.kode_brng = resep_dokter_racikan_detail.kode_brng " +
                                                "where resep_dokter_racikan_detail.no_resep=? " +
                                                "order by databarang.nama_brng");
                    ps12.setString(1,rs2.getString(1));
                    rs11=ps12.executeQuery();
                    
                    //Ambil Resep Obat Manual
                    ps13=koneksi.prepareStatement("SELECT " +
                                                "resep_dokter_rsmg.nm_obat,resep_dokter_rsmg.jml,resep_dokter_rsmg.aturan_pakai " +
                                                "FROM resep_dokter_rsmg " +
                                                "WHERE resep_dokter_rsmg.no_resep=? " +
                                                "ORDER BY resep_dokter_rsmg.nm_obat ASC");
                    ps13.setString(1,rs2.getString(1));
                    rs12=ps13.executeQuery();
                    
                    ps14=koneksi.prepareStatement("select resep_dokter_racikan_rsmg.no_racikan,resep_dokter_racikan_rsmg.nama_racik,resep_dokter_racikan_rsmg.kd_racik, " +
                                                "resep_dokter_racikan_rsmg.jml,resep_dokter_racikan_rsmg.aturan_pakai,resep_dokter_racikan_rsmg.keterangan " +
                                                "from resep_dokter_racikan_rsmg " +
                                                "where resep_dokter_racikan_rsmg.no_resep=? order by resep_dokter_racikan_rsmg.no_racikan ASC");
                    ps14.setString(1,rs2.getString(1));
                    rs13=ps14.executeQuery();
                    
                    nama_obat=""; 
                    
                    if(rs3.next() || rs11.next()){
                        while(rs3.next()){
                            nama_obat = nama_obat + rs3.getString(1)+" ("+rs3.getString(2)+")\n";
                            TindakLanjut.setText(nama_obat);
                        }
                        
                        while(rs11.next()){
                            nama_obat = nama_obat + rs11.getString(1)+"\n";
                            TindakLanjut.setText(nama_obat);
                        }
                    }else{
                        while(rs12.next()){
                            nama_obat = nama_obat + rs12.getString(1)+" ("+rs12.getString(3)+")\n";
                            TindakLanjut.setText(nama_obat);
                        }
                        
                        while(rs13.next()){
                            nama_obat = nama_obat + rs13.getString(2)+"\n";
                            TindakLanjut.setText(nama_obat);
                        }
                    }

                }else{
                    JOptionPane.showMessageDialog(rootPane,"Belum ada Resep Yang diinput..!!");
                    TindakLanjut.requestFocus();
                }
                
            } catch (Exception e) {
                System.out.println("Notifikasi Tampil Obat 2 : "+e);
            }
        } catch (Exception e){
            System.out.println("Notifikasi Tampil Obat 1 : "+e);
        }
        this.setCursor(Cursor.getDefaultCursor());
    }//GEN-LAST:event_BtnCari1ActionPerformed

